import React, { useEffect } from 'react';
import './style/global.css'

const BackgroundStars = () => {
    useEffect(() => {
        const starContainer = document.querySelector(".stars");

        // Normal Twinkling Stars
        for (let i = 0; i < 100; i++) {
            let star = document.createElement("div");
            star.classList.add("star");
            star.style.top = `${Math.random() * 100}vh`;
            star.style.left = `${Math.random() * 100}vw`;
            star.style.animationDuration = `${Math.random() * 2 + 1}s`;
            starContainer.appendChild(star);
        }

        // Shooting Stars Function
        const createShootingStar = () => {
            let shootingStar = document.createElement("div");
            shootingStar.classList.add("shooting-star");

            // Random Start Position (Different Locations)
            let startX = Math.random() * 100;  // Start from any horizontal position
            let startY = Math.random() * 50;   // Start from upper half of the screen

            shootingStar.style.top = `${startY}vh`;
            shootingStar.style.left = `${startX}vw`;

            starContainer.appendChild(shootingStar);

            // Remove star after animation ends
            setTimeout(() => {
                shootingStar.remove();
            }, 2000);
        };

        // Create Shooting Stars Every Few Seconds
        setInterval(createShootingStar, 3000); // Random stars every 3 seconds

    }, []);

    return <div className="stars"></div>;
};

export default BackgroundStars;
