import React, { useEffect } from 'react';
import './style/global.css'

const BackgroundStars = () => {
    useEffect(() => {
        const starContainer = document.querySelector(".stars");


        // Shooting Star Function
        const createShootingStar = () => {
            let shootingStar = document.createElement("div");
            shootingStar.classList.add("shooting-star");

            let tail = document.createElement("div");
            tail.classList.add("shooting-tail");

            // Random Start Position
            let startX = Math.random() * 100;
            let startY = Math.random() * 50;

            shootingStar.style.top = `${startY}vh`;
            shootingStar.style.left = `${startX}vw`;

            tail.style.top = `${startY}vh`;
            tail.style.left = `${startX}vw`;

            starContainer.appendChild(tail);
            starContainer.appendChild(shootingStar);

            // Remove after animation
            setTimeout(() => {
                shootingStar.remove();
                tail.remove();
            }, 2000);
        };

        // Shooting star after 7 to 8 seconds randomly
        const randomTime = () => Math.random() * (8000 - 7000) + 7000; // Between 7s and 8s

        const startShootingStars = () => {
            createShootingStar();
            setTimeout(startShootingStars, randomTime()); // Recursive timeout instead of interval
        };

        setTimeout(startShootingStars, randomTime()); // Start with initial delay

    }, []);

    return <div className="stars"></div>;
};

export default BackgroundStars;
