import React, { useEffect } from "react";
import { gsap } from "gsap";
import './style/global.css'
const Hero = () => {
  useEffect(() => {
    gsap.from(".hero-text", { opacity: 0, y: -50, duration: 1 });
  }, []);

  return (
    <div className="hero">
      <h1 className="hero-text">I am Afnan Fahim</h1>
      <p> Bringing imagination to reality with the power of programming.</p>
    </div>
  );
};

export default Hero;
