import React, { useEffect, useState } from "react";
import './style/global.css';

const CursorEffect = () => {
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [cursorPos, setCursorPos] = useState({ x: 0, y: 0 });
  const speed = 0.05; // ðŸŸ¢ Smooth transition speed

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  useEffect(() => {
    let animationFrame;

    const smoothMove = () => {
      setCursorPos(prevPos => ({
        x: prevPos.x + (mousePos.x - prevPos.x) * speed,
        y: prevPos.y + (mousePos.y - prevPos.y) * speed
      }));
      animationFrame = requestAnimationFrame(smoothMove);
    };

    animationFrame = requestAnimationFrame(smoothMove);
    return () => cancelAnimationFrame(animationFrame);
  }, [mousePos]);

  return (
    <div
      className="light-effect"
      style={{
        transform: `translate(${cursorPos.x}px, ${cursorPos.y}px)`,
      }}
    ></div>
  );
};

export default CursorEffect;
