import React, { useEffect, useState } from "react";
import './style/global.css';

const CursorEffect = () => {
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [cursorPos, setCursorPos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePos({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  useEffect(() => {
    let animationFrameId;

    const smoothMove = () => {
      setCursorPos(prevPos => ({
        x: prevPos.x + (mousePos.x - prevPos.x) * 0.1, // Smooth easing
        y: prevPos.y + (mousePos.y - prevPos.y) * 0.1,
      }));

      animationFrameId = requestAnimationFrame(smoothMove);
    };

    animationFrameId = requestAnimationFrame(smoothMove); // Start animation

    return () => cancelAnimationFrame(animationFrameId);
  }, [mousePos]); // ⚠️ Dependency array hata diya, taake continuously smooth follow kare

  return (
    <div
      className="light-effect"
      style={{
        top: `${cursorPos.y}px`,
        left: `${cursorPos.x}px`,
      }}
    ></div>
  );
};

export default CursorEffect;
